import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { API_URL } from '../../config/constants';
import './Documentation.css';

const Documentation = () => {
  const [docs, setDocs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchDocumentation();
  }, []);

  const fetchDocumentation = async () => {
    try {
      const response = await axios.get(`${API_URL}/documentation`);
      if (response.status === 200) {
        setDocs(response.data);
      }
    } catch (err) {
      setError('Dokümantasyon yüklenirken bir hata oluştu: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="documentation-container">
        <div className="loading">
          <div className="loading-spinner"></div>
          <p>Dokümantasyon yükleniyor...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="documentation-container">
        <div className="error">
          <p>{error}</p>
          <button onClick={fetchDocumentation} className="retry-button">
            Tekrar Dene
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="documentation-container">
      <aside className="sidebar">
        <h1 className="sidebar-title">Dockix Dokümantasyon</h1>

        <ul className="sidebar-menu">
          {docs.map((section) => (
            <li key={section.id} className="menu-item">
              <div className="menu-item-title">{section.title}</div>
              <ul className="submenu">
                {section.subsections.map((subsection) => (
                  <li key={subsection.id} className="submenu-item">
                    <a href={`#${subsection.slug}`}>{subsection.title}</a>
                  </li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      </aside>

      <main className="content">
        {docs.map((section) => (
          <div key={section.id} className="section">
            <h2>{section.title}</h2>
            {section.subsections.map((subsection) => (
              <div key={subsection.id} className="subsection">
                <h3 id={subsection.slug}>{subsection.title}</h3>
                <div dangerouslySetInnerHTML={{ __html: subsection.content }} />
              </div>
            ))}
          </div>
        ))}
      </main>
    </div>
  );
};

export default Documentation;
