import React from "react";
import { likePost } from "../services/api";

const Post = ({ post }) => {
  const handleLike = async () => {
    const updatedPost = { ...post, likes: post.likes + 1 };
    await likePost(post.id, updatedPost);
    alert("Beğenildi!");
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-6 mb-4 hover:shadow-lg transition-shadow">
      <p className="text-gray-800 text-lg mb-4">{post.content}</p>
      <div className="flex justify-between items-center">
        <button 
          onClick={handleLike}
          className="flex items-center gap-2 px-4 py-2 bg-primary text-white rounded-md hover:bg-blue-600 transition-colors"
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
            <path d="M2 10.5a1.5 1.5 0 113 0v6a1.5 1.5 0 01-3 0v-6zM6 10.333v5.43a2 2 0 001.106 1.79l.05.025A4 4 0 008.943 18h5.416a2 2 0 001.962-1.608l1.2-6A2 2 0 0015.56 8H12V4a2 2 0 00-2-2 1 1 0 00-1 1v.667a4 4 0 01-.8 2.4L6.8 7.933a4 4 0 00-.8 2.4z" />
          </svg>
          <span>Beğen ({post.likes})</span>
        </button>
      </div>
    </div>
  );
};

export default Post;
