import axios from "axios";

const API = axios.create({ baseURL: "http://localhost:5002" });

API.interceptors.response.use(
  (response) => response.data,
  (error) => {
    console.error("API Hatası:", error);
    return Promise.reject(error);
  }
);

export const fetchPosts = () => API.get("/posts");
export const createPost = (post) => API.post("/posts", post);
export const likePost = (id, updatedPost) => API.put(`/posts/${id}`, updatedPost);
export const addComment = (postId, comment) =>
  API.put(`/posts/${postId}`, { comment });

export const fetchContainers = () => API.get("/containers");
export const fetchDocumentation = () => API.get("/documentation")

// Örnek API endpoint'i için mock data
const mockDocumentation = [
  {
    id: 1,
    title: "Başlangıç",
    subsections: [
      {
        id: "1-1",
        title: "Kurulum",
        slug: "kurulum",
        content: "<p>Dockix kurulum adımları...</p>"
      },
      {
        id: "1-2",
        title: "Hızlı Başlangıç",
        slug: "hizli-baslangic",
        content: "<p>Dockix'e hızlı başlangıç rehberi...</p>"
      }
    ]
  },
  {
    id: 2,
    title: "Temel Kullanım",
    subsections: [
      {
        id: "2-1",
        title: "Dosya Yönetimi",
        slug: "dosya-yonetimi",
        content: "<p>Dosya yükleme ve yönetim özellikleri...</p>"
      }
    ]
  }
];

// API.js'de documentation endpoint'i için örnek:
API.get('/documentation').reply(200, mockDocumentation);
