import React, { useState, useEffect } from "react";
import { fetchContainers } from "../../services/api";
import "ldrs/ring/dist/style.css"; // Yükleme çemberi için gerekli stil
import Ring from "ldrs/ring"; // Ring bileşeni
import "./DockerStatus.css";

const DockerStatus = () => {
  const [viewMode, setViewMode] = useState("grid");
  const [statusFilter, setStatusFilter] = useState("all");
  const [containers, setContainers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getContainers = async () => {
      setLoading(true);
      try {
        await new Promise((resolve) => setTimeout(resolve, 4000));
        const response = await fetchContainers();
        const containerArray = response?.containers || [];
        setContainers(containerArray);
        setError(null);
      } catch (err) {
        setError("Docker konteynerları yüklenirken bir hata oluştu.");
        console.error("Docker API Hatası:", err);
      } finally {
        setLoading(false);
      }
    };

    getContainers();
    const interval = setInterval(getContainers, 10000);
    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return (
      <div className="docker-status-loading">
        <Ring size={50} color="#0066cc" speed={1.5} /> {/* Yükleme çemberi */}
        <p>Konteyner bilgileri yükleniyor...</p>
      </div>
    );
  }

  if (error) {
    return <div className="docker-status-error">{error}</div>;
  }

  return (
    <div className="docker-status-container">
      <h1 className="docker-status-title">Docker Konteyner Durumu</h1>
      <div className="controls">
        <div className="filter-options">
          <select
            value={statusFilter}
            onChange={(e) => setStatusFilter(e.target.value)}
            className="status-filter"
          >
            <option value="all">Tüm Durumlar</option>
            <option value="running">Çalışıyor</option>
            <option value="exited">Durmuş</option>
            <option value="restarting">Yeniden Başlatılıyor</option>
          </select>
        </div>

        <div className="view-options">
          <button
            className={`view-button ${viewMode === "list" ? "active" : ""}`}
            onClick={() => setViewMode("list")}
            title="Liste Görünümü"
          >
            <svg
              className="view-icon"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M4 6h16M4 12h16M4 18h16"
              />
            </svg>
          </button>
          <button
            className={`view-button ${viewMode === "grid" ? "active" : ""}`}
            onClick={() => setViewMode("grid")}
            title="Grid Görünümü"
          >
            <svg
              className="view-icon"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M4 5a1 1 0 011-1h4a1 1 0 011 1v4a1 1 0 01-1 1H5a1 1 0 01-1-1V5zM14 5a1 1 0 011-1h4a1 1 0 011 1v4a1 1 0 01-1 1h-4a1 1 0 01-1-1V5zM4 15a1 1 0 011-1h4a1 1 0 011 1v4a1 1 0 01-1 1H5a1 1 0 01-1-1v-4zM14 15a1 1 0 011-1h4a1 1 0 011 1v4a1 1 0 01-1 1h-4a1 1 0 01-1-1v-4z"
              />
            </svg>
          </button>
        </div>
      </div>

      <div
        className={viewMode === "grid" ? "containers-grid" : "containers-list"}
      >
        {containers.map((container) => (
          <div key={container.Id} className={`container-card ${container.State.toLowerCase()}`}>
            <div className="container-header">
              <h2>{container.Names[0].replace("/", "")}</h2>
              <span className={`status-badge ${container.State.toLowerCase()}`}>
                {container.State}
              </span>
            </div>
            <div className="container-details">
              <p>
                <strong>ID:</strong> {container.Id.slice(0, 12)}
              </p>
              <p>
                <strong>Image:</strong> {container.Image}
              </p>
              {/* Diğer detaylar */}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DockerStatus;
