import React, { useState } from "react"; // Add useState
import { Link, useNavigate } from "react-router-dom";
import "./Navbar.css";
import "./Login.css";

const Navbar = ({ isLoggedIn, onLogout }) => {
  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-brand">
          <span>Dockix</span>
        </Link>
        <div className="navbar-links">
          {isLoggedIn ? (
            <>
              <Link to="/docker-status" className="nav-link">Docker Durumu</Link>
              <Link to="/profile" className="nav-link">Profil</Link>
              <button 
                onClick={onLogout} 
                className="nav-link"
                style={{ border: 'none', background: 'none', cursor: 'pointer' }}
              >
                Çıkış Yap
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="nav-link">Giriş Yap</Link>
              <Link to="/register" className="nav-link">Kayıt Ol</Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Burada gerçek API çağrısı yapılacak
    // Şimdilik mock login işlemi yapıyoruz
    if (email && password) {
      // Login başarılı olduğunda
      onLogin(true);
      navigate('/'); // Ana sayfaya yönlendir
    }
  };

  return (
    <div className="auth-container">
      <h2>Giriş Yap</h2>
      <form onSubmit={handleSubmit} className="auth-form">
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Şifre"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit" className="auth-button">Giriş Yap</button>
      </form>
    </div>
  );
};

export { Navbar };
export default Login;
