import React, { useState } from "react";
import PostList from "../components/PostList";
import { Link } from "react-router-dom";
import "./Home.css";

const Home = () => {
  const [viewMode, setViewMode] = useState('grid'); // 'grid' veya 'list'

  return (
    <div className="home-container">
      <div className="welcome-section">
        <h1 className="welcome-title">Hoş Geldiniz</h1>
        <p className="welcome-text">Düşüncelerinizi paylaşın, tartışmalara katılın</p>
      </div>

      <Link to="/add-post" className="add-post-button">
        <svg className="add-icon" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
        </svg>
        <span className="button-tooltip">Yeni Gönderi</span>
      </Link>

      <div className="content-section">
        <div className="section-header">
          <h2 className="section-title">Son Gönderiler</h2>
          <div className="view-options">
            <button 
              className={`view-button ${viewMode === 'list' ? 'active' : ''}`}
              onClick={() => setViewMode('list')}
              title="Liste Görünümü"
            >
              <svg className="view-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" />
              </svg>
            </button>
            <button 
              className={`view-button ${viewMode === 'grid' ? 'active' : ''}`}
              onClick={() => setViewMode('grid')}
              title="Grid Görünümü"
            >
              <svg className="view-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 5a1 1 0 011-1h4a1 1 0 011 1v4a1 1 0 01-1 1H5a1 1 0 01-1-1V5zM14 5a1 1 0 011-1h4a1 1 0 011 1v4a1 1 0 01-1 1h-4a1 1 0 01-1-1V5zM4 15a1 1 0 011-1h4a1 1 0 011 1v4a1 1 0 01-1 1H5a1 1 0 01-1-1v-4zM14 15a1 1 0 011-1h4a1 1 0 011 1v4a1 1 0 01-1 1h-4a1 1 0 01-1-1v-4z" />
              </svg>
            </button>
          </div>
        </div>
        <PostList viewMode={viewMode} />
      </div>
    </div>
  );
};

export default Home;
