import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate, Link } from "react-router-dom";
import DockerStatus from "./pages/DockerStatus/DockerStatus";
import Home from "./pages/Home/Home";
import Profile from "./pages/Profile/Profile";
import Login from "./pages/Login/Login";
import Register from "./pages/Register/Register";
import Documentation from "./pages/Documentation/Documentation";
import DashBoard from "./pages/DashBoard/DashBoard";

const Navbar = ({ isLoggedIn, onLogout }) => {
  return (
    <nav className="navbar">
      {/* ... other navbar code ... */}
      <div className="navbar-links">
        {isLoggedIn ? (
          <>
            <Link to="/docker-status" className="nav-link">Docker Durumu</Link>
            <Link to="/profile" className="nav-link">Profil</Link>
            <button 
              onClick={onLogout} 
              className="nav-link"
              style={{ border: 'none', background: 'none', cursor: 'pointer' }}
            >
              Çıkış Yap
            </button>
          </>
        ) : (
          <>
            <Link to="/login" className="nav-link">Giriş Yap</Link>
            <Link to="/register" className="nav-link">Kayıt Ol</Link>
          </>
        )}
      </div>
    </nav>
  );
};

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Check if user is logged in on app load
    const token = localStorage.getItem("token");
    if (token) {
      setIsLoggedIn(true);
    }
  }, []);

  const handleLogin = (status) => {
    setIsLoggedIn(status);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    setIsLoggedIn(false);
    return <Navigate to="/login" />;
  };

  return (
    <Router>
      <Navbar isLoggedIn={isLoggedIn} onLogout={handleLogout} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/add-post" element={<AddPost />} />
        <Route path="/profile" element={isLoggedIn ? <Profile /> : <Navigate to="/login" />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/docker-status" element={isLoggedIn ? <DockerStatus /> : <Navigate to="/login" />} />
        <Route path="/documentation" element={<Documentation />} />
        <Route path="/dashboard" element={<DashBoard />} />
      </Routes>
    </Router>
  );
};

export default App;
