import React, { useState, useEffect } from 'react';
import { fetchDocumentation } from '../../services/api';
import { FaChevronDown, FaChevronRight } from 'react-icons/fa'; // Ok ikonları için

const Documentation = () => {
  const [docs, setDocs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [openSections, setOpenSections] = useState({}); // Her section için açık/kapalı durumu

  // Dokümantasyonu fetch et
  useEffect(() => {
    fetchDocumentation() // Backend'den veri çeker
      .then((data) => {
        setDocs(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  const toggleSection = (sectionId) => {
    setOpenSections(prev => ({
      ...prev,
      [sectionId]: !prev[sectionId]
    }));
  };

  return (
    <div className="documentation-container">
      <aside className="sidebar">
        <h1 className="sidebar-title">Dockix Dokümantasyon</h1>

        <ul className="sidebar-menu">
          {docs.map((section) => (
            <li key={section.id} className="menu-item">
              <div 
                className="menu-item-title"
                onClick={() => toggleSection(section.id)}
              >
                <span className="menu-icon">
                  {openSections[section.id] ? <FaChevronDown /> : <FaChevronRight />}
                </span>
                {section.title}
              </div>
              <ul className={`submenu ${openSections[section.id] ? 'open' : ''}`}>
                {section.subsections.map((subsection) => (
                  <li key={subsection.id} className="submenu-item">
                    <a href={`#${subsection.slug}`}>
                      {subsection.title}
                    </a>
                  </li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      </aside>
      {/* ... content kısmı ... */}
    </div>
  );
};

export default Documentation;
