import React, { useState, useEffect } from "react";
import "./DockerStatus.css";

const DockerStatus = () => {
  const [containers, setContainers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const mockContainers = [
      {
        id: "abc123",
        name: "web-container",
        status: "running",
        image: "node:16-alpine",
        ports: "3000:3000",
        created: "2 hours ago",
        cpu: "0.5%",
        memory: "128MB"
      },
      {
        id: "def456",
        name: "db-container",
        status: "stopped",
        image: "mongo:latest",
        ports: "27017:27017",
        created: "1 day ago",
        cpu: "0%",
        memory: "0MB"
      }
    ];

    const fetchContainers = () => {
      setLoading(true);
      try {
        // Burada gerçek API çağrısı yapılabilir
        setContainers(mockContainers);
        setError(null);
      } catch (err) {
        setError("Docker konteynerları yüklenirken bir hata oluştu.");
      } finally {
        setLoading(false);
      }
    };

    fetchContainers();
    const interval = setInterval(fetchContainers, 5000);
    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return (
      <div className="docker-status-loading">
        <div className="loading-spinner"></div>
        <p>Docker durumu yükleniyor...</p>
      </div>
    );
  }

  if (error) {
    return <div className="docker-status-error">{error}</div>;
  }

  return (
    <div className="docker-status-container">
      <h1 className="docker-status-title">Docker Konteyner Durumu</h1>
      <div className="containers-grid">
        {containers.map((container) => (
          <div 
            key={container.id} 
            className={`container-card ${container.status}`}
          >
            <div className="container-header">
              <h2>{container.name}</h2>
              <span className={`status-badge ${container.status}`}>
                {container.status}
              </span>
            </div>
            <div className="container-details">
              <p><strong>ID:</strong> {container.id}</p>
              <p><strong>Image:</strong> {container.image}</p>
              <p><strong>Ports:</strong> {container.ports}</p>
              <p><strong>Created:</strong> {container.created}</p>
              <p><strong>CPU:</strong> {container.cpu}</p>
              <p><strong>Memory:</strong> {container.memory}</p>
            </div>
            <div className="container-actions">
              {container.status === "stopped" ? (
                <button className="start-button">Başlat</button>
              ) : (
                <button className="stop-button">Durdur</button>
              )}
              <button className="restart-button">Yeniden Başlat</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DockerStatus;
